#!/usr/bin/env bash                                                                                                                                                   # Setting up arguments                                                            
targFile="$1"                                                                      specifier="$2"                                                                     targDest="$3"                                                                                                                                                         # Creating the header and footer variables                                        
head="$specifier"_header.html                                                      foot="$specifier"_footer.html                                                                                                                                         # Combining the values                                                            
cat "$head" "$targFile" "$foot" > "$targDest"
